import React , {useState} from "react";
import Header from "../components/header";
import Footer from "../components/footer";
import Btn from "../components/btn";
import { PizzaData } from "../components/data";
import NotificationCard from "../components/notification";

const backgroundStyle ={
    backgroundImage: "url('data:image/jpeg;base64,')",
    backgroundAttachment: "fixed",
}

export default  function Admin(){
    const [notificationVisible,  setNotificationVisible] = useState(false)
    const [inventoryVisible, setInventoryVisible]  =useState(true)
    const [orderStatusVisibile, setOrderStatusVisibile] = useState(false)

    const toggleOrderStatus = () =>{
        setInventoryVisible(false);
        setOrderStatusVisibile(true);
    }

    const toggleInventory = () =>{
        setOrderStatusVisibile(false)
        setInventoryVisible(true)
    }

    const toggleNotification = () =>{
        setNotificationVisible( !notificationVisible )
    }
    const list =["Pizza Base", "Sauce", "Cheese", "Veggies"]

    function CustomPizzaStock({item}){
        return(
            <div>
                    <div className="bg-black p-5 relative rounded-lg">
                        <h3  className="text-lg font-semibold text-center  text-green-600">{item}</h3>
                        <p className="text-white italic">Quantity Left: <span></span></p>
                        <p className="text-white italic">Quantity Sold: <span></span></p>
                        <div><Btn name={"Add Quantity"}/> </div>
                    </div>
            </div>
        )
    }

    function ReadyPizzaStock(){
        return(
            <div className="p-5 relative flex flex-row flex-wrap justify-between ml-5 mr-5">
                { PizzaData.map((data, index) =>(
                    <div  key={index} className=" bg-black mt-10 text-center rounded-lg p-3 " >
                        <h3 className="text-lg font-semibold text-center  text-green-600 ">{data.category}</h3>
                        <div className="bg-white flex justify-center"> <img className="   h-20  bg-white" src={data.url} alt={data.category}></img></div>
                        <p className="text-white italic">Quantity Left: <span className="text-blue-500 font-bold text-lg  mr-3"> {data.number}</span></p>
                        <p className="text-white italic">Quantity Sold:  <span className="text-blue-500 font-bold text-lg  mr-3"> </span></p>
                        <Btn name={"Add Quantity"}/> 
                    </div>

                )) }
            </div>
        )
    }
    return(
        <>
            <div className="pt-20 bg-fixed  " style={backgroundStyle}>
                <Header showMiniHeader2={true} onNotificationClick={toggleNotification} onOrderStatusClick={toggleOrderStatus} onInventoryClick={toggleInventory}  />
                {inventoryVisible && (
                    <div className="mt-10">
                        <h2  className="text-white text-center font-bold text-2xl">Inventory Management For Custom Made Pizzas</h2>
                        <div className="flex flex-row justify-between ml-20 mr-20">
                        { list.map((item, index) =>(
                            <CustomPizzaStock  key={index} item={item}/>
                        )) }
                        </div>
    
                        <div className="mt-10">
                        <h2  className="text-white text-center font-bold text-2xl">Inventory Management For Already Made Pizzas</h2>
                        <ReadyPizzaStock />
                        </div>
                    </div>
                ) }

                { orderStatusVisibile && (
                    <div className="mt-10">
                        <h2  className="text-white text-center font-bold text-2xl">ORDER STATUS</h2>
                    </div>
                ) }


                { notificationVisible && (
                    <div className="fixed right-0 top-32 z-20 overflow-auto">
                        <NotificationCard  toggleNotification={toggleNotification} />
                    </div>
                ) }
                <Footer  />
            </div>
        </>
    )
}